  -- self.refs.name_label:echo('')
  -- self.refs.class_label:echo('')
  -- self.refs.race_label:echo('')
end

function ui.views.stats_panel:render(state)
  --- create a view instance if there isn't one ---
  if not self.refs.container then
    self:create()
  else 
    self:clear()
  end

  --- parse gmcp state into view properties ---
  local props = {
    str = state.str or '',
    dex = state.dex or '',
    con = state.con or '',
    int = state.int or '',
    wis = state.wis or '',
    cha = state.cha or '',
  }

  --- render view properties ---
  self.refs.stat_labels.str_label:echo([[<center>Str: ]]..props.str..[[</center>]])
  self.refs.stat_labels.dex_label:echo([[<center>Dex: ]]..props.dex..[[</center>]])
  self.refs.stat_labels.con_label:echo([[<center>Con: ]]..props.con..[[</center>]])
  self.refs.stat_labels.int_label:echo([[<center>Int: ]]..props.int..[[</center>]])
  self.refs.stat_labels.wis_label:echo([[<center>Wis: ]]..props.wis..[[</center>]])
  self.refs.stat_labels.cha_label:echo([[<center>Cha: ]]..props.cha..[[</center>]])

	self.refs.container:hide()
end

